import React, { useState } from 'react';
import  axios from 'axios';
import { Link } from 'react-router-dom';
import { useHistory } from 'react-router';
import "./login.css"

const initialValues = {
    username: "",
    password:""
};


export default function Login() {
    const { push } = useHistory();
    const [formValues, setFormValues] = useState(initialValues);

    const handleChange = (e) => {
        setFormValues({
            ...formValues,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('https://watermyplantsweb46.herokuapp.com/api/auth/login', formValues)
        .then((res) => {
            window.localStorage.setItem('token', res.data.token);
            push('/PlantList');
        })
        .catch(err => {
            console.log(err.message);
        })
        .finally(() => {
            setFormValues(formValues)
        })
    };

    return (
        <>
            <div className="login-container">
                <form id="login-form" onSubmit={handleSubmit}>
                    <div className="login-form-header">
                        <h1>Login to view your plants.</h1>
                        <p>Don't have a login? <Link id="signup" to="/signup">Create one!</Link></p>
                    </div>

                    <div className="login-input-container">
                        <div className='form-inputs' id='login-inputs'>
                            <label>Username:</label>
                            <input
                                value={formValues.username}
                                name="username"
                                type="text"
                                onChange={handleChange}
                            />

                            <label>Password:</label>
                            <input
                                value={formValues.password}
                                name="password"
                                type="password"
                                onChange={handleChange}
                            />
                        </div>

                        <div className="errors">

                        </div>

                        <button id="login-button">Login</button>
                    </div>
                </form>
            </div>
        </>
    )
}
